import axios from 'axios';

const API_URL = 'https://doanjava-z61i.onrender.com/api/chamcong';

function getToken() {
  return localStorage.getItem('token');
}

// Lấy tất cả chấm công
export const getAllChamCong = (params = {}) => axios.post(`${API_URL}/getAll`, { ...params, token: getToken() });

// Check-in
export const checkIn = (data) => axios.post(`${API_URL}/checkIn`, { ...data, token: getToken() });

// Check-out
export const checkOut = (data) => axios.post(`${API_URL}/checkOut`, { ...data, token: getToken() });

// Sửa chấm công
export const modifyChamCong = (data) => axios.put(`${API_URL}/modifierChamcong`, { ...data, token: getToken() });
