import React, { useEffect, useState } from 'react';
import { getAllChamCong } from '../api/chamcongApi';

const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  marginTop: 16,
  background: '#fff',
  borderRadius: 10,
  boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
};
const thStyle = {
  background: '#fafbfc',
  fontWeight: 700,
  padding: '10px 12px',
  borderBottom: '2px solid #eee',
  textAlign: 'center',
};
const tdStyle = {
  padding: '10px 12px',
  borderBottom: '1px solid #f0f0f0',
  textAlign: 'center',
};

const ChamCongPage = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await getAllChamCong();
      setData(res.data.data || []);
    } catch (e) {
      alert('Lỗi khi lấy dữ liệu chấm công');
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: 32 }}>
      <h2>Chấm công</h2>
      {loading ? <div>Đang tải dữ liệu...</div> : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>Mã CC</th>
              <th style={thStyle}>Mã NV</th>
              <th style={thStyle}>Họ tên</th>
              <th style={thStyle}>Ngày</th>
              <th style={thStyle}>Giờ vào</th>
              <th style={thStyle}>Giờ ra</th>
              <th style={thStyle}>Loại công</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row, idx) => (
              <tr key={row.macc || row.id || idx}>
                <td style={tdStyle}>{row.macc || row.id || ''}</td>
                <td style={tdStyle}>{row.manv || row.employeeid || ''}</td>
                <td style={tdStyle}>{row.hoten || ''}</td>
                <td style={tdStyle}>{row.ngay || row.date || ''}</td>
                <td style={tdStyle}>{row.giovao || row.checkin || ''}</td>
                <td style={tdStyle}>{row.giora || row.checkout || ''}</td>
                <td style={tdStyle}>{row.loaicong || row.loaicongid || ''}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ChamCongPage;
