import axios from 'axios';

const API_URL = 'https://doanjava-z61i.onrender.com/api/danhgia';
function getToken() {
  return localStorage.getItem('token');
}

// Lấy tất cả đánh giá (có thể lọc theo employeeId, ky, nam)
export const getAllDanhGia = (filter = {}) =>
  axios.post(`${API_URL}/getAll`, { ...filter, token: getToken() });

// Lấy chi tiết 1 đánh giá theo ID
export const getDanhGiaById = (id) =>
  axios.get(`${API_URL}/getById/${id}`);

// Thêm mới đánh giá
export const addDanhGia = (data) =>
  axios.post(`${API_URL}/addDanhGia`, { ...data, token: getToken() });

// Sửa đánh giá
export const updateDanhGia = (id, data) =>
  axios.put(`${API_URL}/updateDanhGia/${id}`, { ...data, token: getToken() });

// Xóa đánh giá
export const deleteDanhGia = (id) =>
  axios.delete(`${API_URL}/deleteDanhGia/${id}`);
