import React, { useState } from 'react';

const dummyDepartments = [
  { id: 1, tenpb: 'Kỹ thuật', truongphong: 'Nguyễn Văn A', soluongnv: 12 },
  { id: 2, tenpb: 'Kế toán', truongphong: 'Trần Thị B', soluongnv: 8 },
];

const PhongBanPage = () => {
  const [departments, setDepartments] = useState(dummyDepartments);
  const [search, setSearch] = useState('');
  const [modalOpen, setModalOpen] = useState(false);
  const [editData, setEditData] = useState(null);

  const filtered = departments.filter(dep =>
    dep.tenpb.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: 32 }}>
      <h2>Danh sách phòng ban</h2>
      <div style={{ display: 'flex', marginBottom: 18, gap: 16 }}>
        <input
          placeholder="Tìm kiếm phòng ban..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 220 }}
        />
        <button
          style={{
            background: '#2962ff', color: '#fff', border: 'none', borderRadius: 6,
            padding: '8px 24px', fontWeight: 600, fontSize: 15, cursor: 'pointer'
          }}
          onClick={() => { setEditData(null); setModalOpen(true); }}
        >
          Thêm phòng ban
        </button>
      </div>
      <table style={{ width: '100%', background: '#fff', borderRadius: 10, boxShadow: '0 2px 8px rgba(0,0,0,0.07)' }}>
        <thead>
          <tr>
            <th style={{ padding: 12, textAlign: 'left' }}>Tên phòng ban</th>
            <th style={{ padding: 12, textAlign: 'left' }}>Trưởng phòng</th>
            <th style={{ padding: 12, textAlign: 'center' }}>Số nhân viên</th>
            <th style={{ padding: 12, textAlign: 'center' }}>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {filtered.map(dep => (
            <tr key={dep.id}>
              <td style={{ padding: 12 }}>{dep.tenpb}</td>
              <td style={{ padding: 12 }}>{dep.truongphong || '-'}</td>
              <td style={{ padding: 12, textAlign: 'center' }}>{dep.soluongnv || 0}</td>
              <td style={{ padding: 12, textAlign: 'center' }}>
                <button onClick={() => { setEditData(dep); setModalOpen(true); }} style={{ marginRight: 8 }}>Sửa</button>
                <button onClick={() => {}} style={{ color: '#e53935' }}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {modalOpen && (
        <div>
          {/* Modal thêm/sửa phòng ban sẽ được bổ sung sau khi có API */}
        </div>
      )}
    </div>
  );
};

export default PhongBanPage;
