import axios from 'axios';

const API_URL = 'https://doanjava-z61i.onrender.com/api/tuyendung';

// Lấy tất cả ứng viên theo JobId
export const getAllCandidatesByJobId = (jobId) => axios.post(`${API_URL}/getAllCandidatesByJobId`, { jobId });

// Lấy tất cả ứng viên đang xét duyệt
export const getAllDangXetDuyet = () => axios.post(`${API_URL}/getAll-DangXetDuyet`);

// Thêm mới ứng viên tuyển dụng
export const addNewTuyenDung = (data) => axios.post(`${API_URL}/addNewTuyenDung`, data);
