import axios from 'axios';

const API_URL = 'https://doanjava-z61i.onrender.com/api/luong';

function authHeaders() {
  const token = localStorage.getItem('token');
  return token ? { Authorization: `Bearer ${token}` } : {};
}

// Lấy tất cả phiếu lương
export const getAllPhieuLuong = () => axios.post(`${API_URL}/getAllPhieuLuong`, {}, { headers: authHeaders() });

// Lấy phiếu lương có lọc
export const getFilteredPhieuLuong = (filter) => axios.post(`${API_URL}/getFilteredPhieuLuong`, filter, { headers: authHeaders() });
