import React from 'react';
import { NavLink } from 'react-router-dom';

const tabs = [
  { path: '/', label: 'Dashboard' },
  { path: '/nhanvien', label: 'Nhân viên' },
  { path: '/chamcong', label: 'Chấm công' },
  { path: '/luong', label: 'Lương' },
  { path: '/tuyendung', label: 'Tuyển dụng' },
  { path: '/danhgia', label: 'Đánh giá' },
];

const Tabs = () => (
  <div style={{
    display: 'flex',
    gap: 32,
    padding: '0 40px',
    borderBottom: '2px solid #f0f0f0',
    marginBottom: 18,
    background: '#fff',
    alignItems: 'center',
    minHeight: 56
  }}>
    {tabs.map(tab => (
      <NavLink
        key={tab.path}
        to={tab.path}
        end={tab.path === '/'}
        style={({ isActive }) => ({
          color: isActive ? '#2962ff' : '#222',
          fontWeight: isActive ? 700 : 500,
          borderBottom: isActive ? '3px solid #2962ff' : '3px solid transparent',
          padding: '12px 0',
          fontSize: 17,
          transition: 'all 0.2s',
          background: isActive ? 'rgba(41,98,255,0.07)' : 'none',
          borderRadius: isActive ? '6px 6px 0 0' : 0
        })}
      >
        {tab.label}
      </NavLink>
    ))}
  </div>
);

export default Tabs;
