import React, { useState } from 'react';
import { PieChart, Pie, Cell, Legend, Tooltip, ResponsiveContainer } from 'recharts';
// TODO: Import API functions for Đánh giá when available

const statusBadge = (type, text) => {
  const map = {
    pending: { bg: '#e3edfd', color: '#2962ff' },
    passed: { bg: '#d4f5e9', color: '#1a7f37' },
    failed: { bg: '#fdeaea', color: '#d32f2f' },
  };
  const style = {
    background: map[type]?.bg,
    color: map[type]?.color,
    padding: '2px 14px',
    borderRadius: 12,
    fontWeight: 500,
    fontSize: 14,
    display: 'inline-block',
  };
  return <span style={style}>{text}</span>;
};

const initialForm = {
  hoten: '',
  phongban: '',
  diem: '',
  nhanxet: '',
  trangthai: '',
};

const DUMMY_DATA = [
  {
    hoten: 'Nguyễn Văn A',
    phongban: 'Kỹ thuật',
    diem: 8.5,
    nhanxet: 'Làm việc tốt',
    trangthai: 'Đạt',
  },
  {
    hoten: 'Trần Thị B',
    phongban: 'Kế toán',
    diem: 6.0,
    nhanxet: 'Cần cải thiện',
    trangthai: 'Chưa đạt',
  },
];

// Phân loại hiệu suất
const getPerformanceLevel = (diem) => {
  if (diem === undefined || diem === null || diem === '') return 'Chưa có';
  const d = parseFloat(diem);
  if (d >= 0 && d <= 4.9) return 'Kém';
  if (d >= 5 && d <= 6.9) return 'Trung bình';
  if (d >= 7 && d <= 7.9) return 'Khá';
  if (d >= 8 && d <= 8.9) return 'Tốt';
  if (d >= 9 && d <= 10) return 'Xuất sắc';
  return 'Chưa có';
};

const COLORS = ['#e57373', '#ffd54f', '#64b5f6', '#81c784', '#ba68c8'];
const PERFORMANCE_LABELS = ['Kém', 'Trung bình', 'Khá', 'Tốt', 'Xuất sắc'];

const getPieData = (evaluations) => {
  const counts = { 'Kém': 0, 'Trung bình': 0, 'Khá': 0, 'Tốt': 0, 'Xuất sắc': 0 };
  evaluations.forEach(ev => {
    const level = getPerformanceLevel(ev.diem);
    if (counts[level] !== undefined) counts[level]++;
  });
  return PERFORMANCE_LABELS.map((label) => ({ name: label, value: counts[label] }));
};

const DanhGiaPage = () => {
  const [evaluations, setEvaluations] = useState(DUMMY_DATA);
  const [loading] = useState(false); // Placeholder for loading state
  const [form, setForm] = useState(initialForm);
  const [adding, setAdding] = useState(false);
  const [addError, setAddError] = useState('');
  const [addSuccess, setAddSuccess] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [search, setSearch] = useState('');

  // Placeholder for API fetch
  // useEffect(() => { ... }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async e => {
    e.preventDefault();
    setAdding(true);
    setAddError('');
    setAddSuccess('');
    // TODO: Replace with API call
    setTimeout(() => {
      setEvaluations(prev => [...prev, form]);
      setAddSuccess('Thêm đánh giá thành công!');
      setForm(initialForm);
      setAdding(false);
    }, 500);
  };

  const filteredEvaluations = evaluations.filter(ev => {
    if (filterStatus !== 'all' && ev.trangthai !== filterStatus) return false;
    if (search && !ev.hoten.toLowerCase().includes(search.toLowerCase())) return false;
    return true;
  });

  const pieData = getPieData(evaluations);

  return (
    <div style={{ padding: 32 }}>
      {/* Biểu đồ hiệu suất */}
      <div style={{ background: '#fff', borderRadius: 12, padding: 32, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', marginBottom: 32 }}>
        <h3 style={{ margin: 0, marginBottom: 18 }}>Hiệu suất công việc (phân bố theo điểm)</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={pieData}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={100}
              label={({ name, value }) => value > 0 ? `${name}: ${value}` : ''}
            >
              {pieData.map((entry, idx) => (
                <Cell key={`cell-${idx}`} fill={COLORS[idx % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip formatter={(value, name) => [`${value} người`, name]} />
            <Legend />
          </PieChart>
        </ResponsiveContainer>
      </div>
      <div style={{ background: '#fff', borderRadius: 12, padding: 32, boxShadow: '0 2px 8px rgba(0,0,0,0.04)' }}>
        <h2 style={{ margin: 0 }}>Bảng đánh giá nhân viên</h2>
        <div style={{ color: '#888', marginBottom: 24 }}>Tổng cộng {evaluations.length} đánh giá</div>
        <div style={{ display: 'flex', gap: 16, marginBottom: 20 }}>
          <input
            type="text"
            placeholder="Tìm kiếm theo tên..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 220 }}
          />
          <select
            value={filterStatus}
            onChange={e => setFilterStatus(e.target.value)}
            style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 180 }}
          >
            <option value="all">Tất cả trạng thái</option>
            <option value="Đạt">Đạt</option>
            <option value="Chưa đạt">Chưa đạt</option>
          </select>
        </div>
        {/* Form thêm đánh giá */}
        <form onSubmit={handleAdd} style={{ display: 'flex', gap: 12, marginBottom: 24, alignItems: 'flex-end', flexWrap: 'wrap' }}>
          <input name="hoten" value={form.hoten} onChange={handleChange} placeholder="Họ tên" required style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 160 }} />
          <input name="phongban" value={form.phongban} onChange={handleChange} placeholder="Phòng ban" required style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 140 }} />
          <input name="diem" value={form.diem} onChange={handleChange} placeholder="Điểm" type="number" step="0.1" required style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 100 }} />
          <input name="nhanxet" value={form.nhanxet} onChange={handleChange} placeholder="Nhận xét" required style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 180 }} />
          <select name="trangthai" value={form.trangthai} onChange={handleChange} required style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 120 }}>
            <option value="">Chọn trạng thái</option>
            <option value="Đạt">Đạt</option>
            <option value="Chưa đạt">Chưa đạt</option>
          </select>
          <button type="submit" disabled={adding} style={{ background: '#2962ff', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 24px', fontWeight: 500, cursor: 'pointer' }}>{adding ? 'Đang thêm...' : 'Thêm đánh giá'}</button>
          {addError && <span style={{ color: 'red', marginLeft: 12 }}>{addError}</span>}
          {addSuccess && <span style={{ color: 'green', marginLeft: 12 }}>{addSuccess}</span>}
        </form>
        {loading ? <div>Đang tải dữ liệu...</div> : (
        <table style={{ width: '100%', borderCollapse: 'collapse', background: '#fff' }}>
          <thead>
            <tr style={{ borderBottom: '2px solid #f0f0f0', textAlign: 'left' }}>
              <th style={{ padding: '12px 8px' }}>Họ tên</th>
              <th style={{ padding: '12px 8px' }}>Phòng ban</th>
              <th style={{ padding: '12px 8px' }}>Điểm</th>
              <th style={{ padding: '12px 8px' }}>Nhận xét</th>
              <th style={{ padding: '12px 8px' }}>Trạng thái</th>
            </tr>
          </thead>
          <tbody>
            {filteredEvaluations.map((ev, idx) => (
              <tr key={idx} style={{ borderBottom: '1px solid #f5f5f5', verticalAlign: 'middle' }}>
                <td style={{ padding: '16px 8px' }}>{ev.hoten}</td>
                <td style={{ padding: '16px 8px' }}>{ev.phongban}</td>
                <td style={{ padding: '16px 8px' }}>{ev.diem}</td>
                <td style={{ padding: '16px 8px' }}>{ev.nhanxet}</td>
                <td style={{ padding: '16px 8px' }}>{statusBadge(ev.trangthai === 'Đạt' ? 'passed' : ev.trangthai === 'Chưa đạt' ? 'failed' : 'pending', ev.trangthai)}</td>
              </tr>
            ))}
          </tbody>
        </table>
        )}
      </div>
    </div>
  );
};

export default DanhGiaPage;
