import React, { useEffect, useState } from 'react';
import { getEmployees, deleteEmployee } from '../api/employeeApi';

const tableStyle = {
  width: '80%',
  margin: '20px auto',
  borderCollapse: 'collapse',
};
const thtdStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  textAlign: 'center',
};
const headerStyle = {
  background: '#f2f2f2',
  fontWeight: 'bold',
};

function EmployeeList({ onEdit, refresh }) {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    fetchEmployees();
    // eslint-disable-next-line
  }, [refresh]);

  const fetchEmployees = async () => {
    try {
      const res = await getEmployees();
      setEmployees(res.data.data || []);
    } catch (error) {
      alert('Lỗi khi lấy danh sách nhân viên');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa nhân viên này?')) {
      await deleteEmployee(id);
      fetchEmployees();
    }
  };

  return (
    <div>
      <h2>Danh sách nhân viên</h2>
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={{...thtdStyle, ...headerStyle}}>STT</th>
            <th style={{...thtdStyle, ...headerStyle}}>Họ tên</th>
            <th style={{...thtdStyle, ...headerStyle}}>Chức danh</th>
            <th style={{...thtdStyle, ...headerStyle}}>Giới tính</th>
            <th style={{...thtdStyle, ...headerStyle}}>Ngày sinh</th>
            <th style={{...thtdStyle, ...headerStyle}}>Địa chỉ</th>
            <th style={{...thtdStyle, ...headerStyle}}>Điện thoại</th>
            <th style={{...thtdStyle, ...headerStyle}}>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp, idx) => (
            <tr key={emp.id || emp.fid}>
              <td style={thtdStyle}>{idx + 1}</td>
              <td style={thtdStyle}>{emp.hoten}</td>
              <td style={thtdStyle}>{emp.chucdanh}</td>
              <td style={thtdStyle}>{emp.gioitinh}</td>
              <td style={thtdStyle}>{emp.ngaysinh}</td>
              <td style={thtdStyle}>{emp.diachi}</td>
              <td style={thtdStyle}>{emp.dienthoai}</td>
              <td style={thtdStyle}>
                <button onClick={() => onEdit(emp)}>Sửa</button>{' '}
                <button onClick={() => handleDelete(emp.id || emp.fid)}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EmployeeList;
